CREATE TABLE IF NOT EXISTS "botuser" (
    "id" SERIAL NOT NULL PRIMARY KEY,
    "tg_id" BIGINT NOT NULL  DEFAULT 0,
    "token" VARCHAR(256) NOT NULL  DEFAULT '',
    "lang" VARCHAR(2) NOT NULL  DEFAULT 'ru'
);
CREATE TABLE IF NOT EXISTS "group" (
    "id" SERIAL NOT NULL PRIMARY KEY,
    "tg_id" BIGINT NOT NULL  DEFAULT 0,
    "username" VARCHAR(255) NOT NULL  DEFAULT '',
    "donation_post" TEXT NOT NULL,
    "is_report_donations" BOOL NOT NULL  DEFAULT True,
    "admin_id" INT NOT NULL REFERENCES "botuser" ("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "aerich" (
    "id" SERIAL NOT NULL PRIMARY KEY,
    "version" VARCHAR(255) NOT NULL,
    "app" VARCHAR(20) NOT NULL,
    "content" TEXT NOT NULL
);
